<?php	header("Access-Control-Allow-Origin: *");	header("Content-Type: application/json; charset=UTF-8");	header("Access-Control-Allow-Methods: POST");	header("Access-Control-Max-Age: 3600");	header("Access-Control-Allow-Headers: Content-Type, Access-Control-Allow-Headers, Authorization, X-Requested-With");	include_once '../config/database.php';	$database = new Database();	$db = $database->getConnection();	$bulk = json_decode(file_get_contents("php://input"));	$stmt = $db->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);	$stmt = $db->beginTransaction(); 	foreach($bulk->records as $data){		try {		$query = "INSERT INTO dep (`depos`,`unit`,`case`,`scat`,`rcat`,`who`,`sent`,`tstamp`,`isdel`,`lreg`,`lstore`)			VALUES 		(:depos,:unit,:case,:scat,:rcat,:who,:sent,now(),0,:lreg,:lstore)		ON DUPLICATE KEY UPDATE		`depos` = :depos,		`unit` = :unit,		`case` = :case,		`scat` = :scat,		`rcat` = :rcat,		`who` = :who,		`sent` = :sent,		`tstamp` = NOW(),		`lstore` = :lstore,		`lreg` = :lreg,		`isdel` = :isdel";		$stmt = $db->prepare($query);		$thisdepos=strip_tags($data->depos);		$thisunit=strip_tags($data->unit);		$thiscase=strip_tags($data->case);		$thisscat=strip_tags($data->scat);		$thisrcat=strip_tags($data->rcat);		$thiswho=strip_tags($data->who);		$thissent=strip_tags($data->sent);		$thislreg=strip_tags($data->lreg);		$thislstore=strip_tags($data->lstore);		$thisisdel=strip_tags($data->isdel);		$thiststamp=strip_tags($data->tstamp);		$stmt->bindParam(':depos', $thisdepos);		$stmt->bindParam(':unit', $thisunit);		$stmt->bindParam(':case', $thiscase);		$stmt->bindParam(':scat', $thisscat);		$stmt->bindParam(':rcat', $thisrcat);		$stmt->bindParam(':who', $thiswho);		$stmt->bindParam(':sent', $thissent);		$stmt->bindParam(':lreg', $thislreg);		$stmt->bindParam(':lstore', $thislstore);		$stmt->bindParam(':isdel', $thisisdel);		$stmt->bindParam(':tstamp', $thiststamp);		$stmt->execute();		} catch (PDOException $e) {			$message = $e->getMessage();			echo $message;		}		}		$spiritsEND=$db->commit();		if($spiritsEND==TRUE){			http_response_code(200);			echo json_encode(array("message" => "UPDATED.."));		}		else{			http_response_code(503);			echo json_encode(array("message" => "ERROR UPDATING!!"));			echo json_encode(array("error" =>$spiritsEND));		}?>