<?php	header("Access-Control-Allow-Origin: *");	header("Content-Type: application/json; charset=UTF-8");	header("Access-Control-Allow-Methods: POST");	header("Access-Control-Max-Age: 3600");	header("Access-Control-Allow-Headers: Content-Type, Access-Control-Allow-Headers, Authorization, X-Requested-With");	include_once '../config/database.php';	$database = new Database();	$db = $database->getConnection();	$bulk = json_decode(file_get_contents("php://input"));	$stmt = $db->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);	$stmt = $db->beginTransaction(); 		foreach($bulk->records as $data){		try {		$query = "INSERT INTO gla (`sequence`,`type`,`glaccount`,`short`,`descript`,`balance`,`lastcheck`,`who`,`qbooks`,`qbooksac`,`skipstat`,`sent`,`tstamp`,`isdel`,`lreg`,`lstore`)			VALUES 		(:sequence,:type,:glaccount,:short,:descript,:balance,:lastcheck,:who,:qbooks,:qbooksac,:skipstat,:sent,now(),0,:lreg,:lstore)		ON DUPLICATE KEY UPDATE		`sequence` = :sequence,		`type` = :type,		`glaccount` = :glaccount,		`short` = :short,		`descript` = :descript,		`balance` = :balance,		`lastcheck` = :lastcheck,		`who` = :who,		`qbooks` = :qbooks,		`qbooksac` = :qbooksac,		`skipstat` = :skipstat,		`sent` = :sent,		`tstamp` = NOW(),		`lstore` = :lstore,		`lreg` = :lreg,		`isdel` = :isdel";		$stmt = $db->prepare($query);		$thissequence=strip_tags($data->sequence);		$thistype=strip_tags($data->type);		$thisglaccount=strip_tags($data->glaccount);		$thisshort=strip_tags($data->short);		$thisdescript=strip_tags($data->descript);		$thisbalance=strip_tags($data->balance);		$thislastcheck=strip_tags($data->lastcheck);		$thiswho=strip_tags($data->who);		$thisqbooks=strip_tags($data->qbooks);		$thisqbooksac=strip_tags($data->qbooksac);		$thisskipstat=strip_tags($data->skipstat);		$thissent=strip_tags($data->sent);		$thislreg=strip_tags($data->lreg);		$thislstore=strip_tags($data->lstore);		$thisisdel=strip_tags($data->isdel);		$thiststamp=strip_tags($data->tstamp);		$stmt->bindParam(':sequence', $thissequence);		$stmt->bindParam(':type', $thistype);		$stmt->bindParam(':glaccount', $thisglaccount);		$stmt->bindParam(':short', $thisshort);		$stmt->bindParam(':descript', $thisdescript);		$stmt->bindParam(':balance', $thisbalance);		$stmt->bindParam(':lastcheck', $thislastcheck);		$stmt->bindParam(':who', $thiswho);		$stmt->bindParam(':qbooks', $thisqbooks);		$stmt->bindParam(':qbooksac', $thisqbooksac);		$stmt->bindParam(':skipstat', $thisskipstat);		$stmt->bindParam(':sent', $thissent);		$stmt->bindParam(':lreg', $thislreg);		$stmt->bindParam(':lstore', $thislstore);		$stmt->bindParam(':isdel', $thisisdel);		$stmt->bindParam(':tstamp', $thiststamp);		$stmt->execute();		} catch (PDOException $e) {			$message = $e->getMessage();			echo $message;		}		}		$spiritsEND=$db->commit();		if($spiritsEND==TRUE){			http_response_code(200);			echo json_encode(array("message" => "UPDATED.."));		}		else{			http_response_code(503);			echo json_encode(array("message" => "ERROR UPDATING!!"));			echo json_encode(array("error" =>$spiritsEND));		}?>