<?php	header("Access-Control-Allow-Origin: *");	header("Content-Type: application/json; charset=UTF-8");	header("Access-Control-Allow-Methods: POST");	header("Access-Control-Max-Age: 3600");	header("Access-Control-Allow-Headers: Content-Type, Access-Control-Allow-Headers, Authorization, X-Requested-With");	include_once '../config/database.php';	$database = new Database();	$db = $database->getConnection();	$bulk = json_decode(file_get_contents("php://input"));	$stmt = $db->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);	$stmt = $db->beginTransaction(); 	foreach($bulk->records as $data){		try {		$query = "INSERT INTO hnh (`listnum`,`listname`,`listtype`,`descript`,`store`,`avg_last`,`posted`,`lastline`,`who`,`tstamp`,`isdel`,`lreg`,`lstore`)			VALUES 		(:listnum,:listname,:listtype,:descript,:store,:avg_last,:posted,:lastline,:who,now(),0,:lreg,:lstore)		ON DUPLICATE KEY UPDATE		`listnum` = :listnum,		`listname` = :listname,		`listtype` = :listtype,		`descript` = :descript,		`store` = :store,		`avg_last` = :avg_last,		`posted` = :posted,		`lastline` = :lastline,		`who` = :who,		`tstamp` = NOW(),		`lstore` = :lstore,		`lreg` = :lreg,		`isdel` = :isdel";		$stmt = $db->prepare($query);		$thislistnum=strip_tags($data->listnum);		$thislistname=strip_tags($data->listname);		$thislisttype=strip_tags($data->listtype);		$thisdescript=strip_tags($data->descript);		$thisstore=strip_tags($data->store);		$thisavg_last=strip_tags($data->avg_last);		$thisposted=strip_tags($data->posted);		$thislastline=strip_tags($data->lastline);		$thiswho=strip_tags($data->who);		$thislreg=strip_tags($data->lreg);		$thislstore=strip_tags($data->lstore);		$thisisdel=strip_tags($data->isdel);		$thiststamp=strip_tags($data->tstamp);		$stmt->bindParam(':listnum', $thislistnum);		$stmt->bindParam(':listname', $thislistname);		$stmt->bindParam(':listtype', $thislisttype);		$stmt->bindParam(':descript', $thisdescript);		$stmt->bindParam(':store', $thisstore);		$stmt->bindParam(':avg_last', $thisavg_last);		$stmt->bindParam(':posted', $thisposted);		$stmt->bindParam(':lastline', $thislastline);		$stmt->bindParam(':who', $thiswho);		$stmt->bindParam(':lreg', $thislreg);		$stmt->bindParam(':lstore', $thislstore);		$stmt->bindParam(':isdel', $thisisdel);		$stmt->bindParam(':tstamp', $thiststamp);		$stmt->execute();		} catch (PDOException $e) {			$message = $e->getMessage();			echo $message;		}		}		$spiritsEND=$db->commit();		if($spiritsEND==TRUE){			http_response_code(200);			echo json_encode(array("message" => "UPDATED.."));		}		else{			http_response_code(503);			echo json_encode(array("message" => "ERROR UPDATING!!"));			echo json_encode(array("error" =>$spiritsEND));		}?>