<?php	header("Access-Control-Allow-Origin: *");	header("Content-Type: application/json; charset=UTF-8");	header("Access-Control-Allow-Methods: POST");	header("Access-Control-Max-Age: 3600");	header("Access-Control-Allow-Headers: Content-Type, Access-Control-Allow-Headers, Authorization, X-Requested-With");	include_once '../config/database.php';	$database = new Database();	$db = $database->getConnection();	$bulk = json_decode(file_get_contents("php://input"));	$stmt = $db->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);	$stmt = $db->beginTransaction(); 	foreach($bulk->records as $data){		try {		$query = "INSERT INTO lookup (`cidkey`,`cname`,`cvalue`,`cdesc`,`tstamp`,`isdel`,`lreg`,`lstore`)			VALUES 		(:cidkey,:cname,:cvalue,:cdesc,now(),0,:lreg,:lstore)		ON DUPLICATE KEY UPDATE		`cidkey` = :cidkey,		`cname` = :cname,		`cvalue` = :cvalue,		`cdesc` = :cdesc,		`tstamp` = NOW(),		`lstore` = :lstore,		`lreg` = :lreg,		`isdel` = :isdel";		$stmt = $db->prepare($query);		$thiscidkey=strip_tags($data->cidkey);		$thiscname=strip_tags($data->cname);		$thiscvalue=strip_tags($data->cvalue);		$thiscdesc=strip_tags($data->cdesc);		$thislreg=strip_tags($data->lreg);		$thislstore=strip_tags($data->lstore);		$thisisdel=strip_tags($data->isdel);		//no need to bind tstamp commented by salman		//$thiststamp=strip_tags($data->tstamp);		$stmt->bindParam(':cidkey', $thiscidkey);		$stmt->bindParam(':cname', $thiscname);		$stmt->bindParam(':cvalue', $thiscvalue);		$stmt->bindParam(':cdesc', $thiscdesc);		$stmt->bindParam(':lreg', $thislreg);		$stmt->bindParam(':lstore', $thislstore);		$stmt->bindParam(':isdel', $thisisdel);		//$stmt->bindParam(':tstamp', $thiststamp);		$stmt->execute();		} catch (PDOException $e) {			$message = $e->getMessage();			echo $message;		}		}		$spiritsEND=$db->commit();		if($spiritsEND==TRUE){			http_response_code(200);			echo json_encode(array("message" => "UPDATED.."));		}		else{			http_response_code(503);			echo json_encode(array("message" => "ERROR UPDATING!!"));			echo json_encode(array("error" =>$spiritsEND));		}?>