<?phpheader("Access-Control-Allow-Origin: *");header("Access-Control-Allow-Headers: access");header("Access-Control-Allow-Methods: GET");header("Access-Control-Allow-Credentials: true");header("Content-Type: application/json");include_once '../config/database.php';include_once '../config/core.php';include_once '../objects/spiritspwd.php';$database = new Database();$db = $database->getConnection();$product = new spiritsPwd($db);$getevent = isset($_GET["event"]) ? $_GET["event"] : 0;$stmt = $product->getPwd($getevent,$records_per_page);$num = $stmt->rowCount();if($num>0){    $products_arr=array();    $products_arr["records"]=array();    while ($row = $stmt->fetch(PDO::FETCH_ASSOC)){		extract($row);		$product_item=array(		"class" => $class,		"event" => $event,		"ask_level" => $ask_level,		"do_level" => $do_level,		"question" => $question,		"scoptosale" => $scoptosale,		"who" => $who,		"parent" => $parent,		"key" => $key,		"image" => $image,		"descript" => $descript,		"editable" => $editable,		"sent" => $sent,		"lreg" => $lreg,		"lstore" => $lstore,		"isdel" => $isdel       );        array_push($products_arr["records"], $product_item);    }		http_response_code(200);		echo json_encode($products_arr);	}else{		http_response_code(404);		echo json_encode(		array("message" => "NOT FOUND.")	);}?>