<?php	header("Access-Control_allow-Origin: *");	header("Content-Type: application/json; charset=UTF-8");	include_once '../config/core.php';	include_once '../shared/utilities.php';	include_once '../config/database.php';	include_once '../objects/spiritsbrw.php';	$database = new Database();	$db = $database->getConnection();	$product = new spiritsbrw($db);	$getSku=isset($_GET['sku']) ? $_GET['sku'] : die();	$stmt = $product->readBrw($getSku);	$num = $stmt->rowCount();	if($num>0){	    $products_arr=array();	    $products_arr["records"]=array();	    while ($row = $stmt->fetch(PDO::FETCH_ASSOC)){			extract($row);			$product_item=array(		"pack" => $pack,		"sku" => $sku,		"sname" => $sname,		"name" => $name,		"stock" => $stock,		"stat" => $stat,		"sloc" => $sloc,		"type" => $type,		"typename" => $typename,		"invclub" => $invclub,		"tags" => $tags,		"lreg" => $lreg,		"lstore" => $lstore,		"isdel" => $isdel	       );	        array_push($products_arr["records"], $product_item);	    }	    http_response_code(200);	    echo json_encode($products_arr);	}else{		http_response_code(404);		echo json_encode(			array("message" => "NOT FOUND.")		);	}?>