<?php	header("Access-Control_allow-Origin: *");	header("Content-Type: application/json; charset=UTF-8");	include_once '../config/core.php';	include_once '../shared/utilities.php';	include_once '../config/database.php';	include_once '../objects/spiritskys.php';	$database = new Database();	$db = $database->getConnection();	$product = new spiritsKys($db);	$getSku=isset($_GET['key']) ? $_GET['key'] : die();	$stmt = $product->readKys($getSku);	$num = $stmt->rowCount();	if($num>0){	    $products_arr=array();	    $products_arr["records"]=array();	    while ($row = $stmt->fetch(PDO::FETCH_ASSOC)){			extract($row);			$product_item=array(		"keyboard" => $keyboard,		"page" => $page,		"key" => $key,		"keyname" => $keyname,		"cat" => $cat,		"receipts" => $receipts,		"customer" => $customer,		"function" => $function,		"change" => $change,		"credit" => $credit,		"balance" => $balance,		"active" => $active,		"security" => $security,		"who" => $who,		"catname" => $catname,		"keyorder" => $keyorder,		"lreg" => $lreg,		"lstore" => $lstore,		"isdel" => $isdel	       );	        array_push($products_arr["records"], $product_item);	    }	    http_response_code(200);	    echo json_encode($products_arr);	}else{		http_response_code(404);		echo json_encode(			array("message" => "NOT FOUND.")		);	}?>