<?php	header("Access-Control_allow-Origin: *");	header("Content-Type: application/json; charset=UTF-8");	include_once '../config/core.php';	include_once '../shared/utilities.php';	include_once '../config/database.php';	include_once '../objects/spiritsvin.php';	$database = new Database();	$db = $database->getConnection();	$product = new spiritsVin($db);	$getSku=isset($_GET['year']) ? $_GET['year'] : die();	$stmt = $product->readVin($getSku);	$num = $stmt->rowCount();	if($num>0){	    $products_arr=array();	    $products_arr["records"]=array();	    while ($row = $stmt->fetch(PDO::FETCH_ASSOC)){			extract($row);			$product_item=array(		"sku" => $sku,		"vintage" => $vintage,		"freeform1" => $freeform1,		"freeform2" => $freeform2,		"freeform3" => $freeform3,		"freeform4" => $freeform4,		"memo" => $memo,		"who" => $who,		"sent" => $sent,		"lreg" => $lreg,		"lstore" => $lstore,		"isdel" => $isdel	       );	        array_push($products_arr["records"], $product_item);	    }	    http_response_code(200);	    echo json_encode($products_arr);	}else{		http_response_code(404);		echo json_encode(			array("message" => "NOT FOUND.")		);	}?>