<?php	header("Access-Control_allow-Origin: *");	header("Content-Type: application/json; charset=UTF-8");	include_once '../config/core.php';	include_once '../shared/utilities.php';	include_once '../config/database.php';	include_once '../objects/spiritsvnd.php';	$database = new Database();	$db = $database->getConnection();	$product = new spiritsVnd($db);	$getSku=isset($_GET['vendor']) ? $_GET['vendor'] : die();	$stmt = $product->readVnd($getSku);	$num = $stmt->rowCount();	if($num>0){	    $products_arr=array();	    $products_arr["records"]=array();	    while ($row = $stmt->fetch(PDO::FETCH_ASSOC)){			extract($row);			$product_item=array(		"vendor" => $vendor,		"firstname" => $firstname,		"lastname" => $lastname,		"vcode" => $vcode,		"street1" => $street1,		"street2" => $street2,		"city" => $city,		"state" => $state,		"zip" => $zip,		"contact" => $contact,		"phone" => $phone,		"fax" => $fax,		"modem" => $modem,		"startdate" => $startdate,		"taxcode" => $taxcode,		"terms" => $terms,		"shipvia" => $shipvia,		"crdlimit" => $crdlimit,		"balance" => $balance,		"who" => $who,		"memo" => $memo,		"includedep" => $includedep,		"sent" => $sent,		"email" => $email,		"useware" => $useware,		"inactive" => $inactive,		"gosent" => $gosent,		"taxable" => $taxable,		"lreg" => $lreg,		"lstore" => $lstore,		"qbooks" => $qbooks,		"isdel" => $isdel	       );	        array_push($products_arr["records"], $product_item);	    }	    http_response_code(200);	    echo json_encode($products_arr);	}else{		http_response_code(404);		echo json_encode(			array("message" => "NOT FOUND.")		);	}?>