<?php	header("Access-Control-Allow-Origin: *");	header("Content-Type: application/json; charset=UTF-8");	header("Access-Control-Allow-Methods: POST");	header("Access-Control-Max-Age: 3600");	header("Access-Control-Allow-Headers: Content-Type, Access-Control-Allow-Headers, Authorization, X-Requested-With");	include_once '../config/database.php';	include_once '../objects/spiritsbrw.php';	$database = new Database();	$db = $database->getConnection();	$spiritsUPDB = new spiritsbrw($db);	$data = json_decode(file_get_contents("php://input"));		$spiritsUPDB->pack = $data->pack;		$spiritsUPDB->sku = $data->sku;		$spiritsUPDB->sname = $data->sname;		$spiritsUPDB->name = $data->name;		$spiritsUPDB->stock = $data->stock;		$spiritsUPDB->tstamp = $data->tstamp;		$spiritsUPDB->stat = $data->stat;		$spiritsUPDB->sloc = $data->sloc;		$spiritsUPDB->type = $data->type;		$spiritsUPDB->typename = $data->typename;		$spiritsUPDB->invclub = $data->invclub;		$spiritsUPDB->tags = $data->tags;		$spiritsUPDB->lreg= $data->lreg;		$spiritsUPDB->lstore= $data->lstore;		$spiritsUPDB->isdel = $data->isdel;	$spiritsRTN = $spiritsUPDB->updateBrw();	if($spiritsRTN=='OK'){	    http_response_code(200);	    echo json_encode(array("message" => "UPDATED.."));	}	else{   		http_response_code(503);		echo json_encode(array("message" => "ERROR UPDATING!!"));    	echo json_encode(array("error" =>$spiritsRTN));	}?>