<?php	header("Access-Control-Allow-Origin: *");	header("Content-Type: application/json; charset=UTF-8");	header("Access-Control-Allow-Methods: POST");	header("Access-Control-Max-Age: 3600");	header("Access-Control-Allow-Headers: Content-Type, Access-Control-Allow-Headers, Authorization, X-Requested-With");	include_once '../config/database.php';	include_once '../objects/spiritschd.php';	$database = new Database();	$db = $database->getConnection();	$spiritsUPDB = new spiritschd($db);	$data = json_decode(file_get_contents("php://input"));		$spiritsUPDB->dl_state = $data->dl_state;		$spiritsUPDB->dl_number = $data->dl_number;		$spiritsUPDB->bankacct = $data->bankacct;		$spiritsUPDB->chk_nbr = $data->chk_nbr;		$spiritsUPDB->chk_dte = $data->chk_dte;		$spiritsUPDB->chk_amt = $data->chk_amt;		$spiritsUPDB->reason = $data->reason;		$spiritsUPDB->pd_amt = $data->pd_amt;		$spiritsUPDB->cntr = $data->cntr;		$spiritsUPDB->file_loc = $data->file_loc;		$spiritsUPDB->status = $data->status;		$spiritsUPDB->customer = $data->customer;		$spiritsUPDB->store = $data->store;		$spiritsUPDB->who = $data->who;		$spiritsUPDB->memo = $data->memo;		$spiritsUPDB->lreg= $data->lreg;		$spiritsUPDB->lstore= $data->lstore;		$spiritsUPDB->isdel = $data->isdel;	$spiritsRTN = $spiritsUPDB->updateChd();	if($spiritsRTN=='OK'){	    http_response_code(200);	    echo json_encode(array("message" => "UPDATED.."));	}	else{   		http_response_code(503);		echo json_encode(array("message" => "ERROR UPDATING!!"));    	echo json_encode(array("error" =>$spiritsRTN));	}?>