<?php	header("Access-Control-Allow-Origin: *");	header("Content-Type: application/json; charset=UTF-8");	header("Access-Control-Allow-Methods: POST");	header("Access-Control-Max-Age: 3600");	header("Access-Control-Allow-Headers: Content-Type, Access-Control-Allow-Headers, Authorization, X-Requested-With");	include_once '../config/database.php';	include_once '../objects/spiritscrd.php';	$database = new Database();	$db = $database->getConnection();	$spiritsUPDB = new spiritscrd($db);	$data = json_decode(file_get_contents("php://input"));		$spiritsUPDB->order = $data->order;		$spiritsUPDB->line = $data->line;		$spiritsUPDB->crdoradj = $data->crdoradj;		$spiritsUPDB->creditpaid = $data->creditpaid;		$spiritsUPDB->crdpaydate = $data->crdpaydate;		$spiritsUPDB->crdpaynbr = $data->crdpaynbr;		$spiritsUPDB->who = $data->who;		$spiritsUPDB->lreg= $data->lreg;		$spiritsUPDB->lstore= $data->lstore;		$spiritsUPDB->isdel = $data->isdel;	$spiritsRTN = $spiritsUPDB->updateCrd();	if($spiritsRTN=='OK'){	    http_response_code(200);	    echo json_encode(array("message" => "UPDATED.."));	}	else{   		http_response_code(503);		echo json_encode(array("message" => "ERROR UPDATING!!"));    	echo json_encode(array("error" =>$spiritsRTN));	}?>