<?php	header("Access-Control-Allow-Origin: *");	header("Content-Type: application/json; charset=UTF-8");	header("Access-Control-Allow-Methods: POST");	header("Access-Control-Max-Age: 3600");	header("Access-Control-Allow-Headers: Content-Type, Access-Control-Allow-Headers, Authorization, X-Requested-With");	include_once '../config/database.php';	include_once '../objects/spiritsfshst.php';	$database = new Database();	$db = $database->getConnection();	$spiritsUPDB = new spiritsfshst($db);	$data = json_decode(file_get_contents("php://input"));		$spiritsUPDB->date = $data->date;		$spiritsUPDB->storeno = $data->storeno;		$spiritsUPDB->saleno = $data->saleno;		$spiritsUPDB->custid = $data->custid;		$spiritsUPDB->dcredits = $data->dcredits;		$spiritsUPDB->who = $data->who;		$spiritsUPDB->timestamp = $data->timestamp;		$spiritsUPDB->credorpts = $data->credorpts;		$spiritsUPDB->sent = $data->sent;		$spiritsUPDB->type = $data->type;		$spiritsUPDB->olddcredit = $data->olddcredit;		$spiritsUPDB->newdcredit = $data->newdcredit;		$spiritsUPDB->lreg= $data->lreg;		$spiritsUPDB->lstore= $data->lstore;		$spiritsUPDB->isdel = $data->isdel;	$spiritsRTN = $spiritsUPDB->updateFshst();	if($spiritsRTN=='OK'){	    http_response_code(200);	    echo json_encode(array("message" => "UPDATED.."));	}	else{   		http_response_code(503);		echo json_encode(array("message" => "ERROR UPDATING!!"));    	echo json_encode(array("error" =>$spiritsRTN));	}?>