<?php	header("Access-Control-Allow-Origin: *");	header("Content-Type: application/json; charset=UTF-8");	header("Access-Control-Allow-Methods: POST");	header("Access-Control-Max-Age: 3600");	header("Access-Control-Allow-Headers: Content-Type, Access-Control-Allow-Headers, Authorization, X-Requested-With");	include_once '../config/database.php';	include_once '../objects/spiritsinvstk.php';	$database = new Database();	$db = $database->getConnection();	$spiritsUPDB = new spiritsinvstk($db);	$data = json_decode(file_get_contents("php://input"));		$spiritsUPDB->sku = $data->sku;		$spiritsUPDB->name = $data->name;		$spiritsUPDB->sname = $data->sname;		$spiritsUPDB->pack = $data->pack;		$spiritsUPDB->type = $data->type;		$spiritsUPDB->store = $data->store;		$spiritsUPDB->stat = $data->stat;		$spiritsUPDB->pvend = $data->pvend;		$spiritsUPDB->lvend = $data->lvend;		$spiritsUPDB->sent = $data->sent;		$spiritsUPDB->ml = $data->ml;		$spiritsUPDB->lreg= $data->lreg;		$spiritsUPDB->lstore= $data->lstore;		$spiritsUPDB->isdel = $data->isdel;	$spiritsRTN = $spiritsUPDB->updateInvstk();	if($spiritsRTN=='OK'){	    http_response_code(200);	    echo json_encode(array("message" => "UPDATED.."));	}	else{   		http_response_code(503);		echo json_encode(array("message" => "ERROR UPDATING!!"));    	echo json_encode(array("error" =>$spiritsRTN));	}?>