<?php	header("Access-Control-Allow-Origin: *");	header("Content-Type: application/json; charset=UTF-8");	header("Access-Control-Allow-Methods: POST");	header("Access-Control-Max-Age: 3600");	header("Access-Control-Allow-Headers: Content-Type, Access-Control-Allow-Headers, Authorization, X-Requested-With");	include_once '../config/database.php';	include_once '../objects/spiritsmsg.php';	$database = new Database();	$db = $database->getConnection();	$spiritsUPDB = new spiritsmsg($db);	$data = json_decode(file_get_contents("php://input"));		$spiritsUPDB->mcode = $data->mcode;		$spiritsUPDB->mnumber = $data->mnumber;		$spiritsUPDB->active = $data->active;		$spiritsUPDB->mtop = $data->mtop;		$spiritsUPDB->mbottom = $data->mbottom;		$spiritsUPDB->who = $data->who;		$spiritsUPDB->sent = $data->sent;		$spiritsUPDB->lreg= $data->lreg;		$spiritsUPDB->lstore= $data->lstore;		$spiritsUPDB->isdel = $data->isdel;	$spiritsRTN = $spiritsUPDB->updateMsg();	if($spiritsRTN=='OK'){	    http_response_code(200);	    echo json_encode(array("message" => "UPDATED.."));	}	else{   		http_response_code(503);		echo json_encode(array("message" => "ERROR UPDATING!!"));    	echo json_encode(array("error" =>$spiritsRTN));	}?>