<?php	header("Access-Control-Allow-Origin: *");	header("Content-Type: application/json; charset=UTF-8");	header("Access-Control-Allow-Methods: POST");	header("Access-Control-Max-Age: 3600");	header("Access-Control-Allow-Headers: Content-Type, Access-Control-Allow-Headers, Authorization, X-Requested-With");	include_once '../config/database.php';	include_once '../objects/spiritspwd.php';	$database = new Database();	$db = $database->getConnection();	$spiritsUPDB = new spiritspwd($db);	$data = json_decode(file_get_contents("php://input"));		$spiritsUPDB->class = $data->class;		$spiritsUPDB->event = $data->event;		$spiritsUPDB->ask_level = $data->ask_level;		$spiritsUPDB->do_level = $data->do_level;		$spiritsUPDB->question = $data->question;		$spiritsUPDB->scoptosale = $data->scoptosale;		$spiritsUPDB->who = $data->who;		$spiritsUPDB->parent = $data->parent;		$spiritsUPDB->key = $data->key;		$spiritsUPDB->image = $data->image;		$spiritsUPDB->descript = $data->descript;		$spiritsUPDB->editable = $data->editable;		$spiritsUPDB->sent = $data->sent;		$spiritsUPDB->lreg= $data->lreg;		$spiritsUPDB->lstore= $data->lstore;		$spiritsUPDB->isdel = $data->isdel;	$spiritsRTN = $spiritsUPDB->updatePwd();	if($spiritsRTN=='OK'){	    http_response_code(200);	    echo json_encode(array("message" => "UPDATED.."));	}	else{   		http_response_code(503);		echo json_encode(array("message" => "ERROR UPDATING!!"));    	echo json_encode(array("error" =>$spiritsRTN));	}?>