<?php	header("Access-Control-Allow-Origin: *");	header("Content-Type: application/json; charset=UTF-8");	header("Access-Control-Allow-Methods: POST");	header("Access-Control-Max-Age: 3600");	header("Access-Control-Allow-Headers: Content-Type, Access-Control-Allow-Headers, Authorization, X-Requested-With");	include_once '../config/database.php';	include_once '../objects/spiritsreports.php';	$database = new Database();	$db = $database->getConnection();	$spiritsUPDB = new spiritsreports($db);	$data = json_decode(file_get_contents("php://input"));		$spiritsUPDB->crepname = $data->crepname;		$spiritsUPDB->creptype = $data->creptype;		$spiritsUPDB->cappname = $data->cappname;		$spiritsUPDB->crptname = $data->crptname;		$spiritsUPDB->rundate = $data->rundate;		$spiritsUPDB->runwho = $data->runwho;		$spiritsUPDB->class = $data->class;		$spiritsUPDB->level = $data->level;		$spiritsUPDB->gph = $data->gph;		$spiritsUPDB->criteria = $data->criteria;		$spiritsUPDB->mquery = $data->mquery;		$spiritsUPDB->who = $data->who;		$spiritsUPDB->parent = $data->parent;		$spiritsUPDB->key = $data->key;		$spiritsUPDB->image = $data->image;		$spiritsUPDB->descript = $data->descript;		$spiritsUPDB->editable = $data->editable;		$spiritsUPDB->lreg= $data->lreg;		$spiritsUPDB->lstore= $data->lstore;		$spiritsUPDB->isdel = $data->isdel;	$spiritsRTN = $spiritsUPDB->updateReports();	if($spiritsRTN=='OK'){	    http_response_code(200);	    echo json_encode(array("message" => "UPDATED.."));	}	else{   		http_response_code(503);		echo json_encode(array("message" => "ERROR UPDATING!!"));    	echo json_encode(array("error" =>$spiritsRTN));	}?>