<?php	header("Access-Control-Allow-Origin: *");	header("Content-Type: application/json; charset=UTF-8");	header("Access-Control-Allow-Methods: POST");	header("Access-Control-Max-Age: 3600");	header("Access-Control-Allow-Headers: Content-Type, Access-Control-Allow-Headers, Authorization, X-Requested-With");	include_once '../config/database.php';	include_once '../objects/spiritsripmfg.php';	$database = new Database();	$db = $database->getConnection();	$spiritsUPDB = new spiritsripmfg($db);	$data = json_decode(file_get_contents("php://input"));		$spiritsUPDB->bdnumber = $data->bdnumber;		$spiritsUPDB->store = $data->store;		$spiritsUPDB->ponumber = $data->ponumber;		$spiritsUPDB->invoice = $data->invoice;		$spiritsUPDB->vcode = $data->vcode;		$spiritsUPDB->amount = $data->amount;		$spiritsUPDB->orddate = $data->orddate;		$spiritsUPDB->recdate = $data->recdate;		$spiritsUPDB->received = $data->received;		$spiritsUPDB->bdorrips = $data->bdorrips;		$spiritsUPDB->lreg= $data->lreg;		$spiritsUPDB->lstore= $data->lstore;		$spiritsUPDB->isdel = $data->isdel;	$spiritsRTN = $spiritsUPDB->updateRipmfg();	if($spiritsRTN=='OK'){	    http_response_code(200);	    echo json_encode(array("message" => "UPDATED.."));	}	else{   		http_response_code(503);		echo json_encode(array("message" => "ERROR UPDATING!!"));    	echo json_encode(array("error" =>$spiritsRTN));	}?>