<?php	header("Access-Control-Allow-Origin: *");	header("Content-Type: application/json; charset=UTF-8");	header("Access-Control-Allow-Methods: POST");	header("Access-Control-Max-Age: 3600");	header("Access-Control-Allow-Headers: Content-Type, Access-Control-Allow-Headers, Authorization, X-Requested-With");	include_once '../config/database.php';	include_once '../objects/spiritstrm.php';	$database = new Database();	$db = $database->getConnection();	$spiritsUPDB = new spiritstrm($db);	$data = json_decode(file_get_contents("php://input"));		$spiritsUPDB->terms = $data->terms;		$spiritsUPDB->descript = $data->descript;		$spiritsUPDB->priority = $data->priority;		$spiritsUPDB->invoice = $data->invoice;		$spiritsUPDB->credit = $data->credit;		$spiritsUPDB->purchase = $data->purchase;		$spiritsUPDB->vcredit = $data->vcredit;		$spiritsUPDB->netdays = $data->netdays;		$spiritsUPDB->billby = $data->billby;		$spiritsUPDB->dueday1 = $data->dueday1;		$spiritsUPDB->dueday2 = $data->dueday2;		$spiritsUPDB->who = $data->who;		$spiritsUPDB->sent = $data->sent;		$spiritsUPDB->lreg= $data->lreg;		$spiritsUPDB->lstore= $data->lstore;		$spiritsUPDB->isdel = $data->isdel;	$spiritsRTN = $spiritsUPDB->updateTrm();	if($spiritsRTN=='OK'){	    http_response_code(200);	    echo json_encode(array("message" => "UPDATED.."));	}	else{   		http_response_code(503);		echo json_encode(array("message" => "ERROR UPDATING!!"));    	echo json_encode(array("error" =>$spiritsRTN));	}?>