<?php	header("Access-Control-Allow-Origin: *");	header("Content-Type: application/json; charset=UTF-8");	header("Access-Control-Allow-Methods: POST");	header("Access-Control-Max-Age: 3600");	header("Access-Control-Allow-Headers: Content-Type, Access-Control-Allow-Headers, Authorization, X-Requested-With");	include_once '../config/database.php';	include_once '../objects/spiritsvbr.php';	$database = new Database();	$db = $database->getConnection();	$spiritsUPDB = new spiritsvbr($db);	$data = json_decode(file_get_contents("php://input"));		$spiritsUPDB->vtype = $data->vtype;		$spiritsUPDB->brand = $data->brand;		$spiritsUPDB->ml = $data->ml;		$spiritsUPDB->vintage = $data->vintage;		$spiritsUPDB->sname = $data->sname;		$spiritsUPDB->descript = $data->descript;		$spiritsUPDB->bestbot1 = $data->bestbot1;		$spiritsUPDB->bestvend1 = $data->bestvend1;		$spiritsUPDB->bestbot2 = $data->bestbot2;		$spiritsUPDB->bestvend2 = $data->bestvend2;		$spiritsUPDB->updown = $data->updown;		$spiritsUPDB->proof = $data->proof;		$spiritsUPDB->firstdate = $data->firstdate;		$spiritsUPDB->lastdate = $data->lastdate;		$spiritsUPDB->sku = $data->sku;		$spiritsUPDB->vendors = $data->vendors;		$spiritsUPDB->clearflag = $data->clearflag;		$spiritsUPDB->dummy = $data->dummy;		$spiritsUPDB->lreg= $data->lreg;		$spiritsUPDB->lstore= $data->lstore;		$spiritsUPDB->isdel = $data->isdel;	$spiritsRTN = $spiritsUPDB->updateVbr();	if($spiritsRTN=='OK'){	    http_response_code(200);	    echo json_encode(array("message" => "UPDATED.."));	}	else{   		http_response_code(503);		echo json_encode(array("message" => "ERROR UPDATING!!"));    	echo json_encode(array("error" =>$spiritsRTN));	}?>