<?php	header("Access-Control-Allow-Origin: *");	header("Content-Type: application/json; charset=UTF-8");	header("Access-Control-Allow-Methods: POST");	header("Access-Control-Max-Age: 3600");	header("Access-Control-Allow-Headers: Content-Type, Access-Control-Allow-Headers, Authorization, X-Requested-With");	include_once '../config/database.php';	include_once '../objects/spiritsvin.php';	$database = new Database();	$db = $database->getConnection();	$spiritsUPDB = new spiritsvin($db);	$data = json_decode(file_get_contents("php://input"));		$spiritsUPDB->sku = $data->sku;		$spiritsUPDB->vintage = $data->vintage;		$spiritsUPDB->freeform1 = $data->freeform1;		$spiritsUPDB->freeform2 = $data->freeform2;		$spiritsUPDB->freeform3 = $data->freeform3;		$spiritsUPDB->freeform4 = $data->freeform4;		$spiritsUPDB->memo = $data->memo;		$spiritsUPDB->who = $data->who;		$spiritsUPDB->sent = $data->sent;		$spiritsUPDB->lreg= $data->lreg;		$spiritsUPDB->lstore= $data->lstore;		$spiritsUPDB->isdel = $data->isdel;	$spiritsRTN = $spiritsUPDB->updateVin();	if($spiritsRTN=='OK'){	    http_response_code(200);	    echo json_encode(array("message" => "UPDATED.."));	}	else{   		http_response_code(503);		echo json_encode(array("message" => "ERROR UPDATING!!"));    	echo json_encode(array("error" =>$spiritsRTN));	}?>