<?php	header("Access-Control-Allow-Origin: *");	header("Content-Type: application/json; charset=UTF-8");	header("Access-Control-Allow-Methods: POST");	header("Access-Control-Max-Age: 3600");	header("Access-Control-Allow-Headers: Content-Type, Access-Control-Allow-Headers, Authorization, X-Requested-With");	include_once '../config/database.php';	include_once '../objects/spiritsvnd.php';	$database = new Database();	$db = $database->getConnection();	$spiritsUPDB = new spiritsvnd($db);	$data = json_decode(file_get_contents("php://input"));		$spiritsUPDB->vendor = $data->vendor;		$spiritsUPDB->firstname = $data->firstname;		$spiritsUPDB->lastname = $data->lastname;		$spiritsUPDB->vcode = $data->vcode;		$spiritsUPDB->street1 = $data->street1;		$spiritsUPDB->street2 = $data->street2;		$spiritsUPDB->city = $data->city;		$spiritsUPDB->state = $data->state;		$spiritsUPDB->zip = $data->zip;		$spiritsUPDB->contact = $data->contact;		$spiritsUPDB->phone = $data->phone;		$spiritsUPDB->fax = $data->fax;		$spiritsUPDB->modem = $data->modem;		$spiritsUPDB->startdate = $data->startdate;		$spiritsUPDB->taxcode = $data->taxcode;		$spiritsUPDB->terms = $data->terms;		$spiritsUPDB->shipvia = $data->shipvia;		$spiritsUPDB->crdlimit = $data->crdlimit;		$spiritsUPDB->balance = $data->balance;		$spiritsUPDB->who = $data->who;		$spiritsUPDB->memo = $data->memo;		$spiritsUPDB->includedep = $data->includedep;		$spiritsUPDB->sent = $data->sent;		$spiritsUPDB->email = $data->email;		$spiritsUPDB->useware = $data->useware;		$spiritsUPDB->inactive = $data->inactive;		$spiritsUPDB->gosent = $data->gosent;		$spiritsUPDB->taxable = $data->taxable;		$spiritsUPDB->lreg= $data->lreg;		$spiritsUPDB->lstore= $data->lstore;		$spiritsUPDB->qbooks= $data->qbooks;		$spiritsUPDB->isdel = $data->isdel;	$spiritsRTN = $spiritsUPDB->updateVnd();	if($spiritsRTN=='OK'){	    http_response_code(200);	    echo json_encode(array("message" => "UPDATED.."));	}	else{  